import React, { useEffect, useState } from "react";
import { ReactSession } from 'react-client-session';
import { Link, useNavigate } from 'react-router-dom';
import Head from '../common/Head';
import Loader from "../common/Loader";
import Header from "../common/Header";
import { KETO_IMG_CDN_LIVE } from "../../config/constants";


export default function Motivation() {

  let history = useNavigate();
  ReactSession.setStoreType('localStorage');
  const [urlPre, setUrlPre] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    if (ReactSession.get('Get_motivation') == undefined) {
      history('/');
    }
    if (window.location.pathname.split('/')[1] === "men") {
      setUrlPre("/men");
    }
    else if (window.location.pathname.split('/')[1] === "women") {
      setUrlPre("/women");
    }
    else if (window.location.pathname.split('/')[1] === "over40") {
      setUrlPre("/over40");
    }
    else {
      setUrlPre("");
    }


    setTimeout(() => {
      setIsLoading(true); // Set loading state to false
    }, 250); // Simulate a 2-second delay


  });

  if (ReactSession.get('Get_motivation') == undefined) {
    return false;
  }


  if (window.performance) {
    if (performance.getEntriesByType("navigation")[0].type == "reload") {
      // history('/');
    }
  }

  const storeClick = (itemName) => {
    // const itemName = item.target.id;
    ReactSession.set('motivation', itemName);
    history(urlPre + "/part8.html");
  }

  //---------------Get data from reactSession----------------------
  var kb_motivation = ReactSession.get('Get_motivation')['categoryItems'];
  const arr = []
  Object.keys(kb_motivation).forEach(key => arr.push({ name: key, value: kb_motivation[key] }))
  // console.log(kb_motivation);

  if (isLoading === false) {
    return (
      <>
        <Loader />
      </>
    );
    return false;
  }


  return (
    <div>
      <Header />
      <div className='bodly'>
        <div className="prgs-wrp">
          <div className="prgsbar" style={{ width: "63%" }}></div>
          <img src={KETO_IMG_CDN_LIVE + "/checked1.webp"} />
        </div>

        <div className='tit'><h1>{ReactSession.get('Get_motivation').title1}</h1></div>

        {
          arr.map((row, index) => (
            <div className={ReactSession.get('part7') && ReactSession.get('part7') == row.value.itemId ? 'boxac' : 'box'} key={index} onClick={() => { storeClick(row.value.itemId) }}>
              <div className='subtit1'><span>{row.value.name}</span></div>
              <div><img src={row.value.imgname} alt={row.value.name} width={32} height={32} /></div>
              {/* {"/assets/images/s" + (index + 1) + ".svg"} */}
            </div>
          ))
        }



      </div>
    </div>
  )
}
