import React, { useEffect, useState } from "react";
import { ReactSession } from 'react-client-session';
import { useNavigate } from "react-router-dom";
import Head from '../common/Head';
import Loader from "../common/Loader";
import Header from "../common/Header";
import { KETO_IMG_CDN_LIVE } from "../../config/constants";



export default function Part8() {



    ReactSession.setStoreType('localStorage');
    let history = useNavigate();
    const [continueButton, setContinueButton] = useState(false);
    const [urlPre, setUrlPre] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [toggleState, setToggleState] = useState(1);
    const [defaultHeight, setDefaultHeight] = useState({ "cm": 0, "feet": 0, "inch": 0 })



    //---------------set height if user again visit on this page------------------------
    const defaultHeightFun = async () => {
        if (ReactSession.get('htype') && ReactSession.get('htype') == "cm") {
            setToggleState(2);
        } else {
            setToggleState(1);
        }

        if (ReactSession.get('ft') != null && ReactSession.get('inch') != null) {
            setDefaultHeight({ "cm": 0, "feet": ReactSession.get('ft'), "inch": ReactSession.get('inch') });
            setContinueButton(true);
            const feetInput = document.getElementById('height_feet_id');
            const inchInput = document.getElementById('height_inch_id');

            if (feetInput) {
                feetInput.value = ReactSession.get('ft');
            }

            if (inchInput) {
                inchInput.value = ReactSession.get('inch');

            }
            if (feetInput && inchInput) {
                setContinueButton(true);
            }



        } else if (ReactSession.get('cm') != null) {
            setDefaultHeight({ "cm": ReactSession.get('cm'), "feet": 0, "inch": 0 });
            setContinueButton(true);
            const cmInput = document.getElementById("height_cm_id");

            if (cmInput) {
                cmInput.value = ReactSession.get('cm');
                setContinueButton(true);
            }



        }


    }



    useEffect(() => {


        if (window.location.pathname.split('/')[1] === "men") {
            setUrlPre("/men");
        }
        else if (window.location.pathname.split('/')[1] === "women") {
            setUrlPre("/women")
        }
        else if (window.location.pathname.split('/')[1] === "over40") {
            setUrlPre('/over40');
        }
        else {
            setUrlPre("");
        }
        setTimeout(() => {
            setIsLoading(true); // Set loading state to false
        }, 250);

        if (!ReactSession.get('htype')) {
            if (toggleState === 1) {
                ReactSession.set('htype', 'feet');
            } else {
                ReactSession.set('htype', 'cm');
            }
        }


        defaultHeightFun();



    }, []);



    //------Validation Variable Initialized------------
    const val_session = ReactSession.get('Get_validation');
    const val_heightCmMin = val_session['heightCmMin'];
    const val_heightCmMax = val_session['heightCmMax'];
    const val_heightFtMin = val_session['heightFtMin'];
    const val_heightftMax = val_session['heightftMax'];
    //------Validation Variable Initialized-----------!



    // --------------------------GET DATA FROM ReactSession (localStorage)------------------------------

    var age_data = ReactSession.get('Get_age')['categoryItems'];
    const data = [];
    Object.keys(age_data).forEach(key => data.push({ value: age_data[key] }));


    //-------------------------------------------------------------------------------------------------

    var height_data = ReactSession.get('Get_height')['categoryItems'];
    const heightdata = [];
    Object.keys(height_data).forEach(key => heightdata.push({ value: height_data[key] }));
    // --------------------------GET DATA FROM ReactSession (localStorage)------------------------------!

    const htypecmId = heightdata[0].value.itemId;
    const htypefeetId = heightdata[1].value.itemId;
    const htypeinchId = heightdata[2].value.itemId;



    //---------------------------Height Management-----------------------------------------!
    if (ReactSession.get('htype') == "" || ReactSession.get('htype') == undefined) {
        var htype = "cm";
        var htypeid = htypecmId;
        ReactSession.set('htype', htype);
        ReactSession.set('hitypeid', "");
        ReactSession.set('htypeid', htypeid);
    }


    //------------------------------------Height Management-----------------------------------!

    if (isLoading === false) {
        return (
            <>
                <Loader />
            </>
        );
        return false;
    }

    //----------------------------------Handle Continue------------------------------------!

    // --------------------------GET DATA FROM ReactSession (localStorage)------------------------------



    // --------------------------GET DATA FROM ReactSession (localStorage)------------------------------!

    const toggleTab = (index) => {
        setToggleState(index);

        if (index === 1) {
            ReactSession.set('htype', 'feet');
        } else {
            ReactSession.set('htype', 'cm');
        }

    }

    const handleFeet = (e) => {

        if (!ReactSession.get('inch')) {
            ReactSession.set('inch', 0);
        }

        if (e > val_heightftMax) {
            document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class='err_popup'>Please enter less than equal to " + val_heightftMax + "</span>";
            setContinueButton(false);
        } else if (e < val_heightFtMin) {
            document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class='err_popup'>Please enter more than equal to " + val_heightFtMin + "</span>";
            setContinueButton(false);
        } else {
            document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class=''></span>";
            ReactSession.set('ft', e)
            setContinueButton(true);
            ReactSession.set('cm', null);
        }

    }

    const handleInch = (e) => {
        if (e > 11) {
            document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class='err_popup'>Please enter less than equal to 11</span>";

        } else if (e < 0) {

            document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class='err_popup'>Please enter more than equal to 0</span>";

        } else {
            document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class=''></span>";
            ReactSession.set('inch', e);
            ReactSession.set('cm', null);
        }

    }

    const handleCm = (e) => {
        if (e > val_heightCmMax) {
            document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class='err_popup'>Please enter less than equal to " + val_heightCmMax + "</span>";
            setContinueButton(false);
        } else if (e < val_heightCmMin) {
            document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class='err_popup'>Please enter more than equal to " + val_heightCmMin + "</span>";
            setContinueButton(false);
        } else {
            document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class=''></span>";
            setContinueButton(true);
            ReactSession.set('inch', null);
            ReactSession.set('ft', null);
            ReactSession.set('cm', e)
        }

    }

    const handleSubmit = e2 => {

        if (ReactSession.get('htype') === "feet") {
            if (ReactSession.get('ft') >= val_heightFtMin && ReactSession.get('ft') <= val_heightftMax && ReactSession.get('inch') <= 11 && ReactSession.get('cm') === null) {
                history(urlPre + '/kb-weight-current.html');
            } else {
                document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class='err_popup'>Enter a value from " + val_heightFtMin + " ft to " + val_heightftMax + " ft and  0 inch to 11 inch";
            }
        } else if (ReactSession.get('htype') === "cm") {
            if (ReactSession.get('cm') >= val_heightCmMin && ReactSession.get('cm') <= val_heightCmMax && ReactSession.get('inch') === null && ReactSession.get('ft') === null) {
                history(urlPre + '/kb-weight-current.html');
            } else {
                document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class='err_popup'>Enter a value from " + val_heightCmMin + " cm to " + val_heightCmMax + " cm ";
            }
        }


        // console.log(ReactSession.get('htype'));
    }



    return (
        <div>
            <Header />
            <div className='bodly'>
                <div className='tit'><h1>{ReactSession.get('Get_height')['title1']}</h1></div>
                <div className='heightbk sp11'>
                    <div onClick={() => toggleTab(1)} className={toggleState === 1 ? "heightbutac" : "heightbut"} ><div>in</div></div>
                    <div onClick={() => toggleTab(2)} className={toggleState === 2 ? "heightbutac" : "heightbut"} ><div>cm</div></div>
                </div>
                <div className={toggleState === 1 ? "content active-content" : "content hideClass"}>
                    <div>
                        <label><input type="number" onInput={(event) => { event.target.value = event.target.value.replace(/[^0-9]*/g, '').slice(0, 1); }} onKeyUp={(e) => { handleFeet(e.target.value) }} maxLength="1" name="ft" placeholder={defaultHeight.feet == 0 ? defaultHeight.feet : '0'} defaultValue={defaultHeight.feet > 0 ? defaultHeight.feet : ''} id="height_feet_id" className="hgtft tit" /><span className="untft">ft</span></label>

                        <label><input type="number" onInput={(event) => { event.target.value = parseInt(event.target.value) > 11 ? '11' : event.target.value.replace(/[^0-9]*/g, '').slice(0, 2); }} onKeyUp={(e) => { handleInch(e.target.value) }} maxLength="2" name="in" placeholder={defaultHeight.inch == 0 ? defaultHeight.inch : '0'} defaultValue={defaultHeight.inch > 0 ? defaultHeight.inch : ''} id="height_inch_id" className="hgtinch" /><span className="untinch">in</span></label>
                    </div>

                    <p className='heightsu'>Enter a value from {val_heightFtMin} ft to {val_heightftMax}ft and  0 inch to 11 inch</p>
                </div>

                <div className={toggleState === 2 ? "content active-content" : "content hideClass"}>

                    <div>
                        <label><input type="number" onInput={(event) => { event.target.value = event.target.value.replace(/[^0-9]*/g, '').slice(0, 3); }} onKeyUp={(e) => { handleCm(e.target.value) }} className="hgtcm" placeholder={defaultHeight.cm == 0 ? defaultHeight.cm : '0'} defaultValue={defaultHeight.cm > 0 ? defaultHeight.cm : ''} id="height_cm_id" /><span className="untxt">cm</span></label>
                    </div>
                    <p className='heightsu'>Enter a value from {val_heightCmMin} to {val_heightCmMax} cm</p>
                </div>


                <div className='hgtnote'>
                    <img src={KETO_IMG_CDN_LIVE + "/point.svg"} alt="Loading" width={16} height={16} loading="lazy" />
                    <div>
                        <p><strong>How tall are you?</strong></p>
                        <p>ketobalanced.com helps you understand the science of ketogenic diet better. why the keto Diet works better as a weight loss program. It creates a personalized keto meal plan with over 15,000 keto recipes to improve your health and weight loss management. Tasty, high Fat, low carb diet recipes, easy to cook and follow !</p>
                    </div>
                </div>


                <div className="err_popup2"><span></span></div>
                {
                    continueButton == true ? (
                        <div><a href={void (0)} onClick={handleSubmit} className="necxbutton1" id="continue">Continue</a></div>
                    ) : (
                        <div><a href={void (0)} onClick={() => { document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class='err_popup'>Enter a value from " + val_heightFtMin + " ft to " + val_heightftMax + "ft and  0 inch to 11 inch</span>" }} className="necxbutton1 disabled" id="continue">Continue</a></div>
                    )
                }


            </div>
        </div >
    )
}
