import React, { useEffect, useState } from "react";
import Header from '../common/Header'
import { ReactSession } from 'react-client-session';
import { Link, useNavigate } from 'react-router-dom';
import Head from '../common/Head'
import Loader from "../common/Loader";
import { KETO_IMG_CDN_LIVE } from "../../config/constants";

export default function Part1() {


    let refresh_redirect = useNavigate();
    ReactSession.setStoreType('localStorage');
    const [urlPre, setUrlPre] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [active, setActive] = useState(null);
    const [continueButton, setContinueButton] = useState(false);


    useEffect(() => {
        if (ReactSession.get('Get_part1') == undefined) {
            refresh_redirect('/');
        }
        if (window.location.pathname.split('/')[1] === "men") {
            setUrlPre("/men");
        }
        else if (window.location.pathname.split('/')[1] === "women") {
            setUrlPre("/women");
        }
        else if (window.location.pathname.split('/')[1] === "over40") {
            setUrlPre("/over40");
        }
        else {
            setUrlPre("");
        }


        setTimeout(() => {
            setIsLoading(true); // Set loading state to false
        }, 250); // Simulate a 2-second delay

        if (ReactSession.get('part1')) {
            setContinueButton(true);
        } else {
            setContinueButton(false);
        }

        // setInterval(() => {
        //     document.getElementsByClassName("err_popup2")[0].innerHTML = "";
        // }, 10000);


    });

    if (ReactSession.get('Get_part1') == undefined) {
        return false;
    }


    // if (window.performance) {
    //     if (performance.getEntriesByType("navigation")[0].type == "reload") {
    //         // refresh_redirect('/');
    //     }
    // }

    const storeClick = (itemName) => {
        // ReactSession.set('part1', itemName);

        if (ReactSession.get('part1')) {
            let savedArr = ReactSession.get('part1').split(',');
            if (savedArr.includes(itemName)) {
                const indexofSavedArr = savedArr.findIndex((element) => element == itemName);
                savedArr.splice(indexofSavedArr, 1);
                ReactSession.set('part1', savedArr.join(','));
            }
            else {
                itemName = itemName + "," + ReactSession.get('part1');
                ReactSession.set('part1', itemName);

            }
            setActive(current => !current);
        }
        else {
            ReactSession.set('part1', itemName)
            setActive(current => !current);
            document.getElementsByClassName("err_popup2")[0].innerHTML = "";
        }
    }

    //---------------Get data from reactSession----------------------
    var part1_data = ReactSession.get('Get_part1')['categoryItems'];
    const arr = []
    Object.keys(part1_data).forEach(key => arr.push({ name: key, value: part1_data[key] }))
    // console.log(part1_data);

    if (isLoading === false) {
        return (
            <>
                <Loader />
            </>
        );
        return false;
    }
    // console.log(arr);
    //---------------Get data from reactSession----------------------


    return (
        <div>

            <Header />
            <div className='bodly'>
                <div className="prgs-wrp">
                    <div className="prgsbar" style={{ width: "18%" }}></div>
                    <img src={KETO_IMG_CDN_LIVE + "/checked1.webp"} />
                </div>
                <div className='tit'><h1>{ReactSession.get('Get_part1')['title1']}</h1></div>
                <p className='sutex'>Please Select At Least 1</p>

                {
                    part1_data.map((row, index) => (
                        <div key={index} className={ReactSession.get('part1') && ReactSession.get('part1').includes(row.itemId) ? "box1ac" : "box1"} onClick={() => { storeClick(row.itemId) }} >
                            <div className='subtit1'>  <img src={row.imgname} alt=' {row.name}' width={34} height={34} /><span> {row.name}</span></div>
                            {/* {"/assets/images/a" + (index + 1) + ".svg"} */}
                            <div className={ReactSession.get('part1') && ReactSession.get('part1').includes(row.itemId) ? "" : "hideClass"}  ><img src={KETO_IMG_CDN_LIVE + "/checked1.webp"} height={17} width={17} alt="checked" /></div>
                        </div>
                    ))
                }

                <p>{ReactSession.get('Get_part1')['description']}</p>


            </div>

            <div className='fxdctabtm'>

                <div className="err_popup2"><span></span></div>

                {
                    continueButton === true ? (
                        <Link to={urlPre + "/kb-social-proof-mix.html"}><div className='necxbutton'>Yes, I Got it</div></Link>
                    ) : (
                        <> <Link to='#' onClick={() => { document.getElementsByClassName("err_popup2")[0].innerHTML = "<span class='err_popup'>Please select atleast one of the above</span>" }}><div className='necxbutton disabled'>Yes, I Got it</div></Link></>

                    )
                }
            </div>

        </div>
    )
}
