import React, { useState, useEffect } from 'react';
import { ReactSession } from "react-client-session";
import $ from 'jquery';
import Header from '../common/Header';
import { useNavigate, Link } from 'react-router-dom';
import { common_icons } from '../../config/imagedata/small_icons';
import { part2_images } from '../../config/imagedata/pages/part2_images';
import SecurePolicy from '../common/SecurePolicy';

export default function Part2() {

    ReactSession.setStoreType("localStorage");
    let history = useNavigate();
    const [isActive, setActive1] = useState(false);
    const [list, setList] = useState([]);
    const [active, setActive] = useState(null);
    const [urlPre, setUrlPre] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        if (ReactSession.get('Get_meat') == undefined) {
            history('/');
        }
        else {
            continueBtnStatus();
        }

        if (window.location.pathname.split('/')[1] === "men") {
            setUrlPre("/men");
        }
        else if (window.location.pathname.split('/')[1] === "women") {
            setUrlPre("/women")
        }
        else if (window.location.pathname.split('/')[1] === "over40") {
            setUrlPre('/over40');
        }
        else {
            setUrlPre("");
        }

        setTimeout(() => {
            setIsLoading(true); // Set loading state to false
        }, 250);

    });



    if (ReactSession.get('Get_meat') == undefined) {
        return false;
    }

    $("#continue").on("click", function () {
        var dataAnswer = $(".selctn-btn.active").length;
        //alert(dataAnswer);
        if (dataAnswer === 0) {
            //$(this).html("Next");
            $("#err_popup").html('<div class="err_popup">Please select an option</div>').fadeIn();
            return false;
        }
        else if (dataAnswer < 3) {
            $("#err_popup")
                .html('<div class="err_popup">Please select at least 3 or more items to make a better and effective meal plan .</div>').fadeIn();
            return false;
        }
        else if (dataAnswer >= 3) {
            alert("test");
            return true;
        }
        //$('.err_popup').hide();
        history(urlPre + "/part3.html");
    });



    /* extract noMeat or vegan id */

    var meat_data = ReactSession.get('Get_meat')['categoryItems'];
    const data = []
    Object.keys(meat_data).forEach(key => data.push({ name: key, value: meat_data[key] }))
    // console.log(data[7].value.name);

    const iamvegetarian = data[6].value.itemId;
    const iamvegon = data[7].value.itemId;
    /* extract noMeat or vegan id */

    const handleClick = event => {
        continueBtnStatus();

        if (ReactSession.get('part2')) {
            let clicked = event;
            // let clicked = event.target.id;

            let savedArr = ReactSession.get('part2').split(',');
            if (savedArr.includes(clicked)) {
                //console.log(savedArr);
                const indexofSavedArr = savedArr.findIndex((element) => element == clicked);
                savedArr.splice(indexofSavedArr, 1);



                if (clicked === iamvegetarian || clicked === iamvegon) {
                    //ReactSession.remove("part2");
                    ReactSession.set('part2', clicked);
                }
                else if (savedArr.includes(iamvegetarian) || savedArr.includes(iamvegon)) {
                    ReactSession.set('part2', clicked);
                }
                else {
                    //console.log(savedArr);
                    //console.log(indexofSavedArr);
                    ReactSession.set("part2", savedArr.join(','));
                }

            }
            else {
                if (clicked === iamvegetarian || clicked === iamvegon) {
                    //ReactSession.remove("part2");
                    ReactSession.set('part2', clicked);
                }
                else if (savedArr.includes(iamvegetarian) || savedArr.includes(iamvegon)) {
                    ReactSession.set('part2', clicked);
                }
                else {
                    clicked = clicked + "," + ReactSession.get('part2');
                    ReactSession.set('part2', clicked);
                }
            }
            setActive(current => !current);
        }
        else {
            let clicked = event;
            ReactSession.set('part2', clicked);
            setActive(current => !current);
        }
        //window.location.reload(false);
    };

    const continueHandle = e => {
        let clicked = e.target.id;
        if (ReactSession.get('part2')) {
            let savedArr = ReactSession.get('part2').split(',');
            if (savedArr.length >= 3 || savedArr.includes(iamvegetarian) || savedArr.includes(iamvegon)) {
                history(urlPre + '/part3.html');
            }
            else {
                $("#err_popup").html('<div class="err_popup">Please select at least 3 or more items to make a better and effective meal plan .</div>').fadeIn();
            }
        }
        else {
            $("#err_popup").html('<div class="err_popup">Please select an option</div>').fadeIn();
        }
    }

    //check onload button is active or not
    const continueBtnStatus = () => {
        if (ReactSession.get('part2')) {
            let savedArr = ReactSession.get('part2').split(',');
            if (savedArr.length >= 3 || savedArr.includes(iamvegetarian) || savedArr.includes(iamvegon)) {
                setActive1(true);
                $("#err_popup").html('').fadeIn();
            }
            else {
                setActive1(false);
            }
        }
        else {
            setActive1(false);
        }
    }

    // if (isLoading === false) {
    //     return (
    //         <>
    //             <Loader />
    //         </>
    //     );
    //     return false;
    // }


    return (
        <div>

            <Header />
            <div className='bodly'>
                <div className="prgs-wrp">
                    <div className="prgsbar" style={{ width: "36%" }}></div>
                    <img src={common_icons["checked1"]} />
                </div>
                <div className='tit'><h1>{ReactSession.get('Get_meat')['title2']}</h1></div>
                <p className='sutex'>Please Select at least 3 / Choose Preferences</p>

                {
                    data.map((rows, Keynumber) => (
                        ReactSession.get('part2') ? (
                            ReactSession.get('part2').includes(rows.value.itemId) ? (

                                <div className={`box1ac ${active == rows.value.itemId && 'box1'}`} key={Keynumber} onClick={() => { handleClick(rows.value.itemId) }} >
                                    <div className='subtit1' ><img src={part2_images["g" + (Keynumber + 1)]} alt='chicken' width={40} height={40} /><span >{rows.value.name}</span></div>
                                    {/* {"/assets/producticon/" + (rows.value.name).replace(/ /g, '') + ".png"} */}
                                    <div><img src={common_icons["checked1"]} height={17} width={17} alt="checked" /></div>
                                </div>

                            ) : (


                                <div className={`box1 ${active == rows.value.itemId && 'box1'}`} key={Keynumber} onClick={() => { handleClick(rows.value.itemId) }}>
                                    <div className='subtit1' ><img src={part2_images["g" + (Keynumber + 1)]} alt={rows.value.name} width={40} height={40} /><span >{rows.value.name}</span></div>

                                </div>

                            )
                        ) : (

                            <div className={`box1 ${active == rows.value.itemId && 'box1'}`} key={Keynumber} onClick={() => { handleClick(rows.value.itemId) }}>
                                <div className='subtit1' ><img src={part2_images["g" + (Keynumber + 1)]} alt={rows.value.name} width={40} height={40} /><span >{rows.value.name}</span></div>

                            </div>

                        )
                    ))

                }

            </div>
            <div className='fxdctabtm'>
                <div id={"err_popup"} style={{ width: "fit-content", margin: "auto" }}></div>
                <a href={void (0)} id="Continue" onClick={continueHandle}>  <div className={isActive ? 'necxbutton' : 'necxbutton disabled'}>Continue</div></a>
                <SecurePolicy common_icons={common_icons} />
            </div>
        </div >
    )
}
