import React, { useEffect, useState } from "react";
import { ReactSession } from 'react-client-session';
import Header from '../common/Header';
import { Link, useNavigate } from 'react-router-dom';
import Head from '../common/Head';
import Loader from "../common/Loader";
import { KETO_IMG_CDN_LIVE } from "../../config/constants";

export default function HappyWeight() {

  let history = useNavigate();
  ReactSession.setStoreType('localStorage');
  const [urlPre, setUrlPre] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {

    if (window.location.pathname.split('/')[1] === "men") {
      setUrlPre("/men");
    }
    else if (window.location.pathname.split('/')[1] === "women") {
      setUrlPre("/women");
    }
    else if (window.location.pathname.split('/')[1] === "over40") {
      setUrlPre("/over40");
    }
    else {
      setUrlPre("");
    }


    setTimeout(() => {
      setIsLoading(true); // Set loading state to false
    }, 250); // Simulate a 2-second delay


  });

  if (ReactSession.get('Get_happy_weight') == undefined) {
    return false;
  }


  if (window.performance) {
    if (performance.getEntriesByType("navigation")[0].type == "reload") {
      // history('/');
    }
  }

  const storeClick = (itemName) => {
    // const itemName = item.target.id;
    ReactSession.set('kb_happy_weight', itemName);
  }

  //---------------Get data from reactSession----------------------
  var kb_happy_weight_data = ReactSession.get('Get_happy_weight')['categoryItems'];
  const arr = []
  Object.keys(kb_happy_weight_data).forEach(key => arr.push({ name: key, value: kb_happy_weight_data[key] }))
  // console.log(kb_happy_weight_data);

  if (isLoading === false) {
    return (
      <>
        <Loader />
      </>
    );
    return false;
  }



  return (
    <div>

      <Header />
      <div className='bodly'>
        <div className="prgs-wrp">
          <div className="prgsbar" style={{ width: "27.5%" }}></div>
          <img src={KETO_IMG_CDN_LIVE + "/checked1.webp"} />
        </div>

        <div className='tit'><h1>{ReactSession.get('Get_happy_weight')['title1']}</h1></div>
        {
          arr.map((row, index) => (
            <Link to={urlPre + "/how-much-time.html"} onClick={() => { storeClick(row.value.itemId) }} key={index}><div className='box'>
              <div className='subtit1'><span>{row.value.name}</span></div>
              <img src={row.value.imgname} alt={row.value.name} width={32} height={32} />
              {/* {"/assets/images/h-icon" + (index + 1) + ".svg"} */}
            </div>
            </Link>
          ))
        }


      </div>
    </div>
  )
}
