import React, { useEffect, useState } from "react";
import Header from '../common/Header';
import { ReactSession } from 'react-client-session';
import { Link, useNavigate } from "react-router-dom";
import Head from '../common/Head';
import Popup from "reactjs-popup";
import { KETO_IMG_CDN_LIVE } from "../../config/constants";



export default function Occasion() {


    let history = useNavigate();
    ReactSession.setStoreType('localStorage');

    const [isLoading, setIsLoading] = useState(false);
    const [active, setActive] = useState(null);
    const [urlPre, setUrlPre] = useState("");
    const [eventDateSection, setEventDateSection] = useState(false);
    const [isDateOpen, setIsDateOpen] = useState(false);
    const [defaultEventDate, setDefaultEventDate] = useState(new Date().toJSON().slice(0, 10));

    //---------------Get data from reactSession----------------------
    var part1_data = ReactSession.get('Get_occasion')['categoryItems'];
    const arr = []
    Object.keys(part1_data).forEach(key => arr.push({ name: key, value: part1_data[key] }))
    // console.log(part1_data);

    useEffect(() => {

        if (window.location.pathname.split('/')[1] === "men") {
            setUrlPre("/men");
        }
        else if (window.location.pathname.split('/')[1] === "women") {
            setUrlPre("/women");
        }
        else if (window.location.pathname.split('/')[1] === "over40") {
            setUrlPre("/over40");
        }
        else {
            setUrlPre("");
        }

        if (ReactSession.get('event_date')) {
            setDefaultEventDate(ReactSession.get('event_date'));
        }


        setTimeout(() => {
            setIsLoading(true);
        }, 250);


    });

    //---------------Get data from reactSession----------------------

    const storeClick = (itemName, value) => {

        if (ReactSession.get('occasion')) {
            ReactSession.set('occasion', itemName);
            setActive(current => !current);
        }
        else {
            ReactSession.set('occasion', itemName)
            setActive(current => !current);
        }


        if (value === "No") {
            setEventDateSection(false);
            history(urlPre + "/kb-monthly-result.html");
        } else {
            setEventDateSection(true);
            setIsDateOpen(true)
        }

    }

    //-----------date popup-------------------------------
    const hideDatePopup = () => {
        setIsDateOpen(false);
    }
    //-----------date popup-------------------------------



    return (
        <div>
            <Header />

            {
                eventDateSection === false || eventDateSection === true ? (
                    <>
                        <div className='bodly'>
                            <div className='tit'><h1>Is there a special occasion you want to lose weight for?</h1></div>
                            <div className='sutex'>You’re more likely to reach your goal if you have something important to aim for</div><br />



                            {
                                arr.map((row, index) => (
                                    <a href={void (0)} key={index} onClick={() => { storeClick(row.value.itemId, row.value.name) }}> <div className={ReactSession.get('occasion') && ReactSession.get('occasion').includes(row.value.itemId) ? 'box1ac' : 'box1'}>
                                        <div className='subtit1'><img src={row.value.imgname} alt="loading" width={32} height={32} loading="lazy" /><span>{row.value.name}</span></div>
                                        {/* {"/assets/images/o" + (index + 1) + ".svg"} */}
                                        <div className={ReactSession.get('occasion') && ReactSession.get('occasion').includes(row.value.itemId) ? "" : "hideClass"}  >
                                            <img src={KETO_IMG_CDN_LIVE + "/checked1.webp"} height={17} width={17} alt="checked" /></div>
                                    </div>
                                    </a>
                                ))
                            }

                        </div>
                        <Popup open={isDateOpen} closeOnDocumentClick onClose={hideDatePopup}>

                            <div className='bodly'>
                                <div className='tit'><h1>When's your event?</h1></div>
                                <div className='sutex'>Once we know this, we’ll be able to put together a personalized plan to help you get in shape. Your data will not be shared with any third parties.</div>
                                <div className='sp11'>
                                    <label className="dt-lable">
                                        (Optional)
                                    </label><br />
                                    <input type="date" min={new Date().toJSON().slice(0, 10)} placeholder="dd/mm/yyyy" onChange={(event) => { ReactSession.set('event_date', event.target.value) }} defaultValue={defaultEventDate} />

                                </div>
                                <Link to={urlPre + '/kb-monthly-result.html'}>
                                    <div className='sp11'>
                                        <div className='necxbutton1'>Continue</div>
                                    </div>
                                </Link>
                                <a href={void (0)} className='skip-dt' onClick={() => { history(urlPre + '/kb-monthly-result.html') }} >  Skip this question.</a>
                            </div>
                        </Popup>

                    </>
                ) : (
                    <>
                    </>
                )
            }
        </div>
    )
}
