import React, { useEffect, useState } from "react";
import { ReactSession } from 'react-client-session';
import { Link, useNavigate } from 'react-router-dom';
import Header from "../common/Header";
import Loader from "../common/Loader";
import { social_images } from "../../config/imagedata/images_data";

export default function SocialProofMix() {

  let refresh_redirect = useNavigate();
  ReactSession.setStoreType('localStorage');
  const [urlPre, setUrlPre] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [genderContent, setGenderContent] = useState("Men");

  useEffect(() => {

    if (window.location.pathname.split('/')[1] === "men") {
      setUrlPre("/men");
    }
    else if (window.location.pathname.split('/')[1] === "women") {
      setUrlPre("/women");
    }
    else if (window.location.pathname.split('/')[1] === "over40") {
      setUrlPre("/over40");
    }
    else {
      setUrlPre("");
    }

    //----------------- gender filtration------------------------
    if (ReactSession.get('ketobalanced_options')) {
      const genderList = JSON.parse(ReactSession.get('ketobalanced_options')).survey.surveyItems[0].categoryItems;
      const genderName = genderList.filter(genderItem => genderItem.itemId == ReactSession.get('gender'));
      setGenderContent(genderName[0].name);
    }

    // console.log(genderList);
    //-------------- gender filtration-----------------------------

    setTimeout(() => {
      setIsLoading(true); // Set loading state to false
    }, 250); // Simulate a 2-second delay


  });

  if (ReactSession.get('Get_social_proof_mix') == undefined) {
    refresh_redirect('/');
    return false;
  }


  // if (window.performance) {
  //   if (performance.getEntriesByType("navigation")[0].type == "reload") {
  //     // refresh_redirect('/');
  //   }
  // }

  const storeClick = item => {
    const itemName = item.target.id;
    ReactSession.set('kb_social_proof_mix', itemName);
  }

  //---------------Get data from reactSession----------------------
  var spm_data = ReactSession.get('Get_social_proof_mix');

  // if (isLoading === false) {
  //   return (
  //     <>
  //       <Loader />
  //     </>
  //   );
  //   return false;
  // }
  // console.log(arr);
  //---------------Get data from reactSession----------------------


  return (
    <div>

      <Header />
      <div className='bodly'>
        <div className='tit sclprf'><h1>{spm_data.title1}</h1></div>
        <div className='sutex'><strong>{spm_data.title2}</strong></div>
        {
          spm_data.categoryItems.map((row, index) => (
            genderContent.toLowerCase() === row.subcat ? (
              <div className='sp scl-prf-img' key={index}><img src={social_images[genderContent.toLowerCase() + '_g' + (index + 1)]} alt="Loading.." width={400} height={372} /></div>
            ) : (
              <span key={index}></span>
            )
          ))
        }

        {/* {"/assets/images/" + genderContent + "-track-hrs.svg"} */}
        <div className='sp11'>
          <Link to={urlPre + '/kb-target-zones-mix.html'}> <div className='necxbutton1'>Continue</div></Link>
        </div>
      </div>
    </div>
  )
}
